import React from "react"
import Header from "./components/header"
import Users from "./components/users"
import AddUser from "./components/adduser"

class App extends React.Component {
  constructor(props) {
    super(props)
    this.state = {
        users: [
            {
                id: 1,
                firstname: 'Tolya',
                lastname: 'Stepanov',
                bio: 'asd',
                age: 23,
                isHappy: true
            },
            {
                id: 2,
                firstname: 'Jora',
                lastname: 'Dedyukin',
                bio: 'qwe',
                age: 24,
                isHappy: false
            },
            {
                id: 3,
                firstname: 'Alyona',
                lastname: 'Maximova',
                bio: 'zxc',
                age: 23,
                isHappy: true
            }
        ]
    }
}
  render() {
    return (<div>
      <Header title="Список пользователей" />
      <main>
        <Users users= {this.state.users} />
      </main>
      <aside>
        <AddUser onAdd/>
      </aside>
    </div>)
  }

  AddUser (user) {
    console.log (user)
  }
}

export default App
